pragma solidity 0.8.9;

import {StringCastLib} from '../libraries/StringCastLib.sol';
import {ShiftLib} from '../libraries/ShiftLib.sol';
import {Pixel} from '../types/Pixel.sol';
import {Version} from '../types/Version.sol';

library Svg {
    using StringCastLib for uint256;
    using Pixel for uint256;

    function build(
        uint256[] memory file,
        uint256 width,
        uint256 height,
        uint8 zoom
    ) internal pure returns (bytes memory res) {
        bytes memory header = abi.encodePacked(
            "<svg Box='0 0 ", //"<svg Box='0 0 ",
            (zoom * width).toAsciiString(),
            hex'20', // ' ',
            (zoom * width).toAsciiString(),
            "' width='", //"' width='",
            (zoom * width).toAsciiString(),
            "' height='", //  "' height='",
            (zoom * width).toAsciiString(),
            "' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink'>"
        );

        bytes memory footer = hex'3c2f7376673e';

        uint256 last = Version.getPixelAt(file, 0, 0, width).rgba();
        uint256 count = 1;

        // bytes[] memory rects = new bytes[](35);
        bytes memory body;

        for (uint256 y = 0; y < height; y++) {
            for (uint256 x = 0; x < height; x++) {
                if (y == 0 && x == 0) x++;
                uint256 curr = Version.getPixelAt(file, x, y, width);
                if (curr.rgba() == last) {
                    count++;
                    continue;
                } else {
                    body = abi.encodePacked(body, getRekt(last, (x - count) * zoom, y * zoom, 1 * zoom, count * zoom));
                    last = curr;
                    count = 1;
                }
            }

            body = abi.encodePacked(body, getRekt(last, (width - count) * zoom, y * zoom, 1 * zoom, count * zoom));
            last = 0;
            count = 0;
        }

        res = abi.encodePacked(header, body, footer);
    }

    function getRekt(
        uint256 pixel,
        uint256 x,
        uint256 y,
        uint256 xlen,
        uint256 ylen
    ) internal pure returns (bytes memory res) {
        if (pixel & 0xff == 0) return '';
        res = abi.encodePacked(
            "<rect fill='#",
            pixel.rgba().toHexStringNoPrefix(4),
            hex'2720783d27',
            x.toAsciiString(),
            hex'2720793d27',
            y.toAsciiString(),
            hex'27206865696768743d27',
            xlen.toAsciiString(),
            hex'272077696474683d27',
            ylen.toAsciiString(),
            "'/>"
        );
    }
}
","keccak256":"0x89631330fff5ee90eedbfa5cdddea97352bbde3b528681b67c05574e0572d912","license":"MIT"},"src/types/Pixel.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import {SafeCastLib} from '../libraries/SafeCastLib.sol';
import {ShiftLib} from '../libraries/ShiftLib.sol';

library Pixel {
    using SafeCastLib for uint256;

    function safePack(
        uint256 _rgb,
        uint256 _a,
        uint256 _id,
        uint256 _zindex,
        uint256 _feature
    ) internal pure returns (uint256 res) {
        unchecked {
            res |= uint256(_feature.safe3()) << 39;
            res |= uint256(_zindex.safe4()) << 35;
            res |= uint256(_id.safe8()) << 27;
            res |= uint256(_rgb) << 3;
            res |= uint256(compressA(_a.safe8()));
        }
    }

    function unsafePack(
        uint256 _rgb,
        uint256 _a,
        uint256 _id,
        uint256 _zindex,
        uint256 _feature
    ) internal pure returns (uint256 res) {
        unchecked {
            res |= _feature << 39;
            res |= _zindex << 35;
            res |= _id << 27;
            res |= _rgb << 3;
            res |= compressA(uint8(_a));
        }
    }

    function rgba(uint256 input) internal pure returns (uint256 res) {
        return ((input << 5) & 0xffffff_00) | a(input);
    }

    function r(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 19) & 0xff;
    }

    function g(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 11) & 0xff;
    }

    function b(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 3) & 0xff;
    }

    // 3 bits
    function a(uint256 input) internal pure returns (uint256 res) {
        res = decompressA(uint8(input & 0x7));
    }

    // this is 1-8 so 3 bits
    function id(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 27) & 0xff;
    }

    // 18 3,3,4 && 8
    // this is 1-16 so 4 bits
    function z(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 35) & 0xf;
    }

    // this is 1-8 so 3 bits
    function f(uint256 input) internal pure returns (uint256 res) {
        res = (input >> 39) & 0x7;
    }

    /// @notice check for if a pixel exists
    /// @dev for a pixel to exist a must be > 0, so we can safely assume that if we see
    /// no data it is empty or a transparent pixel we do not need to process
    function e(uint256 input) internal pure returns (bool res) {
        res = input != 0x00;
    }

    /// @notice converts an 8 bit (0-255) value into a 3 bit value (0-7)
    /// @dev a compressed value of 7 is equivilent to 255, and a compressed 0 is 0
    function compressA(uint256 input) internal pure returns (uint256 res) {
        return input.safe8() / 36;
    }

    /// @notice converts an 8 bit value into a 3 bit value
    function decompressA(uint256 input) internal pure returns (uint256 res) {
        if (input == 7) return 255;
        else return input.safe3() * 36;
    }
}
