// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import {StringCastLib} from '../libraries/StringCastLib.sol';
import {ShiftLib} from '../libraries/ShiftLib.sol';
import {Pixel} from '../types/Pixel.sol';
import {Version} from '../types/Version.sol';

import {IDotnuggV1Metadata} from '../interfaces/IDotnuggV1Metadata.sol';

library DotnuggV1SvgLib {
    using StringCastLib for uint256;
    using StringCastLib for uint8;

    using StringCastLib for address;

    using Pixel for uint256;

    function build(
        IDotnuggV1Metadata.Memory memory data,
        uint256[] memory file,
        uint256 width,
        uint256 height,
        uint8 zoom
    ) internal pure returns (bytes memory res) {
        bytes memory header = abi.encodePacked(
            "<svg Box='0 0 ", //"<svg Box='0 0 ",
            (zoom * width).toAsciiString(),
            hex'20', // ' ',
            (zoom * width).toAsciiString(),
            "' width='", //"' width='",
            (zoom * width).toAsciiString(),
            "' height='", //  "' height='",
            (zoom * width).toAsciiString(),
            "' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink'>"
        );

        bytes memory footer = hex'3c2f7376673e';

        (uint256 last, ) = Version.getPixelAt(file, 0, 0, width);
        uint256 count = 1;

        // bytes[] memory rects = new bytes[](35);
        bytes memory body;

        for (uint256 y = 0; y < height; y++) {
            for (uint256 x = 0; x < width; x++) {
                if (y == 0 && x == 0) x++;
                (uint256 curr, ) = Version.getPixelAt(file, x, y, width);
                if (curr.rgba() == last.rgba()) {
                    count++;
                    continue;
                } else {
                    body = abi.encodePacked(body, getRekt(last, (x - count) * zoom, y * zoom, 1 * zoom, count * zoom));
                    last = curr;
                    count = 1;
                }
            }

            body = abi.encodePacked(body, getRekt(last, (width - count) * zoom, y * zoom, 1 * zoom, count * zoom));
            last = 0;
            count = 0;
        }

        res = abi.encodePacked(header, body, getMetadata(data), footer);
    }

    function getRekt(
        uint256 pixel,
        uint256 x,
        uint256 y,
        uint256 xlen,
        uint256 ylen
    ) internal pure returns (bytes memory res) {
        if (pixel.a() == 0) return '';
        res = abi.encodePacked(
            "<rect fill='#",
            pixel.rgba().toHexStringNoPrefix(4),
            hex'2720783d27',
            x.toAsciiString(),
            hex'2720793d27',
            y.toAsciiString(),
            hex'27206865696768743d27',
            xlen.toAsciiString(),
            hex'272077696474683d27',
            ylen.toAsciiString(),
            "'/>"
        );
    }

    function getMetadata(IDotnuggV1Metadata.Memory memory data) internal pure returns (bytes memory res) {
        res = abi.encodePacked(
            '<text x="10" y="20" font-family="monospace" font-size="20px" style="fill:black;">Metadata:',
            getTspan(45, 0, 'name', data.name),
            getTspan(75, 0, 'description', data.desc),
            getTspan(105, 0, 'id', data.tokenId.toAsciiString()),
            getTspan(135, 0, 'owner', data.owner.toHexString()),
            getTspan(165, 0, 'items', '')
        );

        for (uint256 i = 0; i < 8; i++) {
            if (data.ids[i] > 0) {
                res = abi.encodePacked(res, getTspan(195 + i * 30, 20, data.labels[i], data.ids[i].toAsciiString()));
            }
        }

        res = abi.encodePacked(res, '</text>');
    }

    function getTspan(
        uint256 y,
        uint256 xoffset,
        string memory label,
        string memory data
    ) internal pure returns (bytes memory res) {
        res = abi.encodePacked(
            '<tspan x="',
            (10 + xoffset).toAsciiString(),
            '" y="',
            y.toAsciiString(),
            '">',
            '<tspan style="font-weight:bold;">',
            label,
            ':</tspan> ',
            data,
            '</tspan>'
        );
    }
}

src/types/Version.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import {ShiftLib} from '../libraries/ShiftLib.sol';
import {BitReader} from '../libraries/BitReader.sol';
import {SafeCastLib} from '../libraries/SafeCastLib.sol';

import {Pixel} from '../types/Pixel.sol';

import '../_test/utils/console.sol';

library Version {
    using BitReader for BitReader.Memory;
    using SafeCastLib for uint256;

    struct Memory {
        uint256[] pallet;
        uint256[] minimatrix;
        uint256[] bigmatrix;
        uint256 receivers;
        uint256 data;
    }
    event log_named_bytes32(string key, bytes32 val);

    function parsePallet(
        BitReader.Memory memory reader,
        uint256 id,
        uint256 feature
    ) internal view returns (uint256[] memory res) {
        uint256 palletLength = reader.select(4) + 1;

        res = new uint256[](palletLength + 1);

        for (uint256 i = 0; i < palletLength; i++) {
            // uint256 working = 0;

            // 4 bits: zindex
            // working |= (reader.select(4) << 32);
            uint256 z = reader.select(4);

            uint256 color;

            uint256 selecta = reader.select(1);
            if (selecta == 1) {
                color = 0x000000;
            } else {
                uint256 r = reader.select(8);
                uint256 g = reader.select(8);
                uint256 b = reader.select(8);

                color = (r << 16) | (g << 8) | b;
            }

            // // 1 or 8 bits: a
            uint256 a = (reader.select(1) == 0x1 ? 0xff : reader.select(8));

            res[i + 1] = Pixel.safePack(color, a, id, z, feature);
        }
    }

    function parseData(
        BitReader.Memory memory reader,
        uint256 feature,
        uint8[] memory xovers,
        uint8[] memory yovers
    ) internal pure returns (uint256 res) {
        // 12 bits: coordinate - anchor x and y

        res |= feature << 75;

        uint256 width = reader.select(6);
        uint256 height = reader.select(6);

        res |= height << 69; // heighth and width
        res |= width << 63;

        uint256 anchorX = reader.select(6);
        uint256 anchorY = reader.select(6);

        if (xovers.length == 8 && yovers.length == 8 && (xovers[feature] != 0 || yovers[feature] != 0)) {
            res |= uint256(yovers[feature]) << 57;
            res |= uint256(xovers[feature]) << 51;
        } else {
            // 12 bits: coordinate - anchor x and y
            res |= anchorX << 51;
            res |= anchorY << 57;
        }

        // 1 or 25 bits: rlud - radii
        res |= (reader.select(1) == 0x1 ? 0x000000 : reader.select(24)) << 27;

        // 1 or 25 bits: rlud - expanders
        res |= (reader.select(1) == 0x1 ? 0x000000 : reader.select(24)) << 3;
    }

    function parseReceivers(BitReader.Memory memory reader) internal pure returns (uint256 res) {
        uint256 receiversLength = reader.select(1) == 0x1 ? 0x1 : reader.select(4);

        for (uint256 j = 0; j < receiversLength; j++) {
            uint256 receiver = 0;

            uint256 yOrYOffset = reader.select(6);

            uint256 xOrPreset = reader.select(6);

            // rFeature
            uint256 rFeature = reader.select(3);

            uint256 calculated = reader.select(1);

            if (calculated == 0x1) {
                receiver |= yOrYOffset << 6;
                receiver |= xOrPreset;
            } else {
                receiver |= xOrPreset << 6;
                receiver |= yOrYOffset;
            }

            receiver <<= ((rFeature * 12) + (calculated == 0x1 ? 128 : 0));

            res |= receiver;
        }
    }

    function parseMiniMatrix(
        BitReader.Memory memory reader,
        uint256 height,
        uint256 width
    ) internal pure returns (uint256[] memory res) {
        uint256 groupsLength = reader.select(1) == 0x1 ? reader.select(8) + 1 : reader.select(16) + 1;

        res = new uint256[]((height * width) / 64 + 1);

        uint256 index = 0;

        for (uint256 a = 0; a < groupsLength; a++) {
            uint256 len = reader.select(2) + 1;

            if (len == 4) len = reader.select(4) + 4;

            uint256 key = reader.select(4);

            for (uint256 i = 0; i < len; i++) {
                res[index / 64] |= (key << (4 * (index % 64)));
                index++;
            }
        }
    }

    function getReceiverAt(
        Memory memory m,
        uint256 index,
        bool calculated
    )
        internal
        pure
        returns (
            uint256 x,
            uint256 y,
            bool exists
        )
    {
        uint256 data = m.receivers >> (index * 12 + (calculated ? 128 : 0));

        data &= ShiftLib.mask(12);

        x = data & ShiftLib.mask(6);
        y = data >> 6;

        exists = x != 0 || y != 0;
    }

    function setReceiverAt(
        Memory memory m,
        uint256 index,
        bool calculated,
        uint256 x,
        uint256 y
    ) internal pure returns (uint256 res) {
        // yOrYOffset
        res |= y << 6;

        //xOrPreset
        res |= x;

        m.receivers |= res << ((index * 12) + (calculated ? 128 : 0));
    }

    function setOffset(
        Memory memory m,
        bool negX,
        uint256 diffX,
        bool negY,
        uint256 diffY
    ) internal pure {
        m.data |= ((((diffX & 0xff) << 1) | (((negX ? 0x1 : 0x0)))) << 85);
        m.data |= ((((diffY & 0xff) << 1) | ((((negY ? 0x1 : 0x0))))) << 94);
    }

    function getOffset(Memory memory m)
        internal
        pure
        returns (
            bool negX,
            uint256 diffX,
            bool negY,
            uint256 diffY
        )
    {
        uint256 data = m.data;
        negX = (data >> 85) & 0x1 == 1;
        diffX = (data >> 86) & 0xff;
        negY = (data >> 94) & 0x1 == 1;
        diffY = (data >> 95) & 0xff;
    }

    function setZ(Memory memory m, uint256 z) internal pure {
        require(z <= 0xf, 'VERS:SETZ:0');
        m.data |= z << 78;
    }

    function getZ(Memory memory m) internal pure returns (uint256 res) {
        res = (m.data >> 78) & 0xf;
    }

    function setFeature(Memory memory m, uint256 z) internal pure {
        require(z <= ShiftLib.mask(3), 'VERS:SETF:0');
        m.data &= ShiftLib.fullsubmask(3, 75);
        m.data |= (z << 75);
    }

    function getFeature(Memory memory m) internal pure returns (uint256 res) {
        res = (m.data >> 75) & ShiftLib.mask(3);
    }

    function getWidth(Memory memory m) internal pure returns (uint256 width, uint256 height) {
        // yOrYOffset
        width = (m.data >> 63) & ShiftLib.mask(6);
        height = (m.data >> 69) & ShiftLib.mask(6);
    }

    function setWidth(
        Memory memory m,
        uint256 w,
        uint256 h
    ) internal pure {
        require(w <= ShiftLib.mask(6), 'VERS:SETW:0');
        require(h <= ShiftLib.mask(6), 'VERS:SETW:1');

        m.data &= ShiftLib.fullsubmask(12, 63);

        m.data |= (w << 63);
        m.data |= (h << 69);
    }

    function getAnchor(Memory memory m) internal pure returns (uint256 x, uint256 y) {
        // yOrYOffset
        x = (m.data >> 51) & ShiftLib.mask(6);
        y = (m.data >> 57) & ShiftLib.mask(6);
    }

    function getOverrides(Memory memory m)
        internal
        pure
        returns (
            bool shouldOverride,
            uint8 x,
            uint8 y
        )
    {
        // yOrYOffset
        x = uint8((m.data >> 78) & ShiftLib.mask(6));
        y = uint8((m.data >> 84) & ShiftLib.mask(6));

        shouldOverride = x != 0 && y != 0;
    }

    function getPixelAt(
        Memory memory m,
        uint256 x,
        uint256 y
    ) internal pure returns (uint256 palletKey) {
        (uint256 width, ) = getWidth(m);
        uint256 index = x + (y * width);

        if (index / 64 >= m.minimatrix.length) return 0x0;

        palletKey = (m.minimatrix[index / 64] >> (4 * (index % 64))) & 0xf;
    }

    function getPalletColorAt(Memory memory m, uint256 index)
        internal
        pure
        returns (
            uint256 res,
            uint256 color,
            uint256 zindex
        )
    {
        // res = (m.pallet[index / 7] >> (36 * (index % 7))) & ShiftLib.mask(36);
        res = m.pallet[index];

        color = Pixel.rgba(res);

        zindex = Pixel.z(res);
    }

    function initBigMatrix(Memory memory m, uint256 width) internal pure {
        m.bigmatrix = new uint256[](((width * width) / 6) + 2);
    }

    function setBigMatrixPixelAt(
        Memory memory m,
        uint256 x,
        uint256 y,
        uint256 color
    ) internal pure {
        (uint256 width, ) = getWidth(m);

        uint256 index = x + (y * width);

        setBigMatrixPixelAt(m, index, color);
    }

    function setBigMatrixPixelAt(
        Memory memory m,
        uint256 index,
        uint256 color
    ) internal pure {
        if (m.bigmatrix.length > index / 6) {
            uint8 offset = (42 * (index % 6)).safe8();
            m.bigmatrix[index / 6] &= ShiftLib.fullsubmask(42, offset);
            m.bigmatrix[index / 6] |= (color << offset);
        }
    }

    function getBigMatrixPixelAt(
        Memory memory m,
        uint256 x,
        uint256 y
    ) internal pure returns (uint256 res) {
        (uint256 width, ) = getWidth(m);

        (res, ) = getPixelAt(m.bigmatrix, x, y, width);
    }

    function getPixelAt(
        uint256[] memory arr,
        uint256 x,
        uint256 y,
        uint256 width
    ) internal pure returns (uint256 res, uint256 row) {
        uint256 index = x + (y * width);

        if (index / 6 >= arr.length) return (0, 0);

        row = (arr[index / 6] >> (42 * (index % 6)));

        res = row & ShiftLib.mask(42);
    }

    function bigMatrixHasPixelAt(
        Memory memory m,
        uint256 x,
        uint256 y
    ) internal pure returns (bool res) {
        uint256 pix = getBigMatrixPixelAt(m, x, y);

        res = pix & 0x7 != 0x00;
    }

    function setArrayLength(uint256[] memory input, uint256 size) internal pure {
        assembly {
            let ptr := mload(input)
            ptr := size
            mstore(input, ptr)
        }
    }
}

